import React, { useState } from 'react'
import query from '../modules/PlayerSelector'

export default function PlayerSelector(props) {

  const teamNames = ['Arsenal', 'Aston Villa', 'Bournemouth', 'Brighton', 'Burnley', 'Chelsea', 'Crystal Palace', 'Everton', 'Leicester City', 'Liverpool', 'Man City', 'Man Utd', 'Newcastle', 'Norwich', 'Sheffield Utd', 'Southampton', 'Spurs', 'Watford', 'West Ham', 'Wolves']

  const [loading, setLoading] = useState(true)
  const [playerId, setPlayerId] = useState(1)
  const [playerNames, setPlayerNames] = useState([])

  const getInitialState = async () => {
    const result = await query(1, 'team')
    setPlayerNames(result)
    setLoading(false)
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    const result = await query(playerId, 'player', playerNames)
    props.addPlayer(result)
  }

  const handleTeamChange = async (e) => {
    const result = await query(e.target.value, 'team') 
    setPlayerNames(result)
  }

  const handlePlayerChange = (e) => {
    setPlayerId(e.target.value)
  }

  let body = <div></div>

  if (loading) {
    getInitialState()
  } else {
