import axios from 'axios'


export default function PlayerSelector(props) {

    const [playerName, setPlayerName] = useState('')

    const types = ['GK', 'DEF', 'MID', 'FWD']
    const pos = types[props.type - 1]

    const handleClick = async () => {
        try {
          const { data: { [0]: { web_name, total_points, id, element_type } = {} } } = await axios.get(`http://localhost:5000/players/${playerName}`)
    
          if(!web_name) {
            throw new Error(`Can't find this player.`)
          }
          if(!(element_type == props.type)) {
              console.log(element_type)
            throw new Error(`${web_name} is not a ${pos}.`)
          }
          
          console.log(web_name, total_points, pos)  
        } catch (e) {
          console.log(e)
        }
      }
    
      const handleChange = (e) => {
        setPlayerName(e.target.value)
      }

    return (
        <div className="App">
            <input value={GK1} onChange={handleChange}></input>
            <button onClick={handleClick}>request</button>
        </div>
    )
}
