export default function addToTeam(player, currentPlayers, maxAmount, team) {
    const prevPlayers = currentPlayers
    const prevValue = getTeamValue(team, currentPlayers, 4)
    let newPlayers = []
    if( currentPlayers.length === maxAmount ) {
        currentPlayers.shift()     
        newPlayers = [...currentPlayers, player]
    } 
    newPlayers = [...currentPlayers, player]

    const newValue = getTeamValue(team, newPlayers, player.element_type - 1)

    let result = {}
    validateTeam(newPlayers, newValue) ? result = { newPlayers, newValue } : result = { newPlayers: prevPlayers, newValue: prevValue }

    return result
}


// Abstract cause mad gross ------------------------------------------------------------
const getTeamValue = ({ goalkeepers, defenders, midfielders, forwards }, newPlayers, position) => {

    let gkVal = 0, defVal = 0, midVal = 0, fwdVal = 0, newPlayersVal = 0
    goalkeepers.forEach(gk => {
        gkVal += gk.now_cost
    })
    defenders.forEach(def => {
        defVal += def.now_cost
    })
    midfielders.forEach(mid => {
        midVal += mid.now_cost
    })
    forwards.forEach(fwd => {
        fwdVal += fwd.now_cost
    })
    newPlayers.forEach(player => {
        newPlayersVal += player.now_cost
    })

    const totals = [gkVal, defVal, midVal, fwdVal, newPlayersVal]
    totals[position] = 0
    return totals.reduce((a,b) => a + b, 0) / 10
}


const validateTeam = (players, teamValue) => {
    if (teamValue > 100) {
        console.log('Team value too high.')
        return false
    }
    if (isPlayerDuplicate(players)) {
        console.log('Player already in team.')
    }

    return true
}

const isPlayerDuplicate = (players) => {
    const playerIds = players.map(player => player.id)
    console.log(playerIds)
}
