export default function addToTeam(player, currentPlayers, maxAmount, team) {
    const prevPlayers = currentPlayers
    const prevValue = getTeamValue(team, currentPlayers, 4)
    let newPlayers = []
    if( currentPlayers.length === maxAmount ) {
        currentPlayers.shift()     
        newPlayers = [...currentPlayers, player]
    } 
    newPlayers = [...currentPlayers, player]

    const newValue = getTeamValue(team, newPlayers, player.element_type - 1)

    let result = {}
    const { result: isValidated, message } = validateTeam(newPlayers, newValue, prevPlayers, player) 

    isValidated ? result = { newPlayers, newValue } : result = { newPlayers: prevPlayers, newValue: prevValue, message }

    return result
}


