const getTeamValue = ({ goalkeepers, defenders, midfielders, forwards }, newPlayers, position) => {

    let gkVal = 0, defVal = 0, midVal = 0, fwdVal = 0, newPlayersVal = 0
    goalkeepers.forEach(gk => {
        gkVal += gk.now_cost
    })
    defenders.forEach(def => {
        defVal += def.now_cost
    })
    midfielders.forEach(mid => {
        midVal += mid.now_cost
    })
    forwards.forEach(fwd => {
        fwdVal += fwd.now_cost
    })
    newPlayers.forEach(player => {
        newPlayersVal += player.now_cost
    })

    const totals = [gkVal, defVal, midVal, fwdVal, newPlayersVal]
    totals[position] = 0
    return totals.reduce((a,b) => a + b, 0) / 10
}


const validateTeam = (newPlayers, teamValue, prevPlayers, player) => {
    if (teamValue > 100) {
        return { result: false, message: 'Team value too high.' }
    }
    if (isPlayerInTeam(player, prevPlayers)) {
        return { result: false, message: 'Player already in team.' }
    }

    return true
}

const isPlayerInTeam = (player, prevPlayers) => {
    const playerIds = prevPlayers.map(player => player.id)
    return (playerIds.includes(player.id))
}
