import axios from 'axios'

export default async function queryBackEnd(query, type) {
    if (type === 'team') {
        const result = await updatePlayerNames(query)
        console.log(result)
        return result
    }
    return getPlayer(query)
}

const updatePlayerNames = async (Id) => {
    try {
      const { data: playerNames } = await axios.get(`http://localhost:5000/teams/${Id}`)
      console.log(`http://localhost:5000/teams/${Id}`, playerNames)
      return playerNames
    } catch (e) {
      console.log(e)
    }
  }

const getPlayer = async (playerId) => {
    const { data } = await axios.get(`http://localhost:5000/players/${playerId}`)
    return data[0]
  }
