export default function addToTeam(player, currentPlayers, maxAmount, team) {
    const prevPlayers = currentPlayers
    let newPlayers = []
    if( currentPlayers.length === maxAmount ) {
        currentPlayers.shift()     
        newPlayers = [...currentPlayers, player]
    } 
    newPlayers = [...currentPlayers, player]

    const newValue = getTeamValue(team, newPlayers, position)

    validateTeam(prevPlayers, newValue)

    return { newPlayers, newValue }
}


// Abstract ------------------------------------------------------------
const getTeamValue = ({ goalkeepers, defenders, midfielders, forwards }, newPlayers, position) => {

    let gkVal = 0, defVal = 0, midVal = 0, fwdVal = 0, newPlayersVal = 0
    goalkeepers.forEach(gk => {
        gkVal += gk.now_cost
    })
    defenders.forEach(def => {
        defVal += def.now_cost
    })
    midfielders.forEach(mid => {
        midVal += mid.now_cost
    })
    forwards.forEach(fwd => {
        fwdVal += fwd.now_cost
    })
    newPlayers.forEach(player => {
        newPlayersVal += newPlayers.now_cost
    })

    return (gkVal + defVal + midVal + fwdVal + newPlayersVal) / 10
}


const validateTeam = (players, teamValue) => {
    if (teamValue > 100) {
        return false
    }

    console.log(players)
}
