import React, { useState } from 'react'
import PlayerSelector from './PlayerSelector'
import addToTeam from '../modules/TeamSelector'

export default function TeamSelector(props) {

    const [goalkeepers, setGoalkeepers] = useState([])
    const [defenders, setDefenders] = useState([])
    const [midfielders, setMidfielders] = useState([])
    const [forwards, setForwards] = useState([])
    const [teamValue, setTeamValue] = useState(0)
    const [message, setMessage] = useState()

    if (props.user[0].team) {
        setGoalkeepers(props.user[0].team.goalkeepers)
        setDefenders(props.user[0].team.defenders)
        setMidfielders(props.user[0].team.midfielders)
        setForwards(props.user[0].team.forwards)
    }

    const addPlayer = async (player) => {
        const team = { goalkeepers, defenders, midfielders, forwards }
        switch (player.element_type) {
            case 1:
                const { newPlayers: newGKs, newValue: newValue1, message: message1 = null } = await addToTeam(player, goalkeepers, 2, team)
                setGoalkeepers(newGKs)
                setTeamValue(newValue1)
                setMessage(message1)
                break          
            case 2:
                const { newPlayers: newDEFs, newValue: newValue2, message: message2 = null } = await addToTeam(player, defenders, 5, team)
                setDefenders(newDEFs)
                setTeamValue(newValue2)
                setMessage(message2)
                break                 
            case 3:
                const { newPlayers: newMIDs, newValue: newValue3, message: message3 = null } = await addToTeam(player, midfielders, 5, team)
                setMidfielders(newMIDs)
                setTeamValue(newValue3)
                setMessage(message3)
                break   
            case 4:
                const { newPlayers: newFWDs, newValue: newValue4, message: message4 = null } = await addToTeam(player, forwards, 3, team)
                setForwards(newFWDs)
                setTeamValue(newValue4)
                setMessage(message4)
                break 
            default:
                setMessage('Error adding player.')    
        }       
    }

    const handleReset = () => {
        setGoalkeepers([])
        setDefenders([])
        setMidfielders([])
        setForwards([])
        setTeamValue(0)
        setMessage()
    }

    const displayPosition = (position) => {
        return position.map(player => player.web_name + `  `)
    }

    const handleSave = () => {
        addToTeam('save', { goalkeepers, defenders, midfielders, forwards }, props.user[0]._id)
    }

    return (
        <div>
            <PlayerSelector addPlayer={addPlayer}/>
            <div>
                Team Value: {teamValue}
            </div>
            <div>
                Goalkeepers: { displayPosition(goalkeepers) }
            </div>
            <div>
                Defenders: { displayPosition(defenders) }
            </div>
            <div>
                Midfielders: { displayPosition(midfielders) }
            </div>
            <div>
                Forwards: { displayPosition(forwards) }
            </div>
            <div>
                <button onClick={handleSave}>Save team</button>
                <button onClick={handleReset}>Clear</button>
            </div>
            <div>
                {message}
            </div>
        </div>
    )
}
