import React, { useState } from 'react'
import PlayerSelector from './PlayerSelector'
import addToTeam from '../utils/teamBuilderUtils'

export default function TeamSelector() {

    const [teamValue, setTeamValue] = useState(0)
    const [goalkeepers, setGoalkeepers] = useState([])
    const [defenders, setDefenders] = useState([])
    const [midfielders, setMidfielders] = useState([])
    const [forwards, setForwards] = useState([])

    // const addToTeam = (player, goalkeepers, midfielders, defenders, forwards) => {
    //     const { element_type } = player
    //     if (element_type === 1) {
    //         if( goalkeepers.length === 2 ) {
    //             goalkeepers.shift()
    //             setGoalkeepers([...goalkeepers, player])
    //         } else {
    //             setGoalkeepers(goalkeepers => [...goalkeepers, player])
    //         }
    //     } else if (element_type === 2) {
    //         if( defenders.length === 5 ) {
    //             defenders.shift()
    //             setDefenders([...defenders, player])
    //         } else {
    //             setDefenders(defenders => [...defenders, player])
    //         }
    //     } else if (element_type === 3) {
    //         if( midfielders.length >= 5 ) {
    //             setTeamValue(teamValue - midfielders.shift().now_cost)
    //             setMidfielders([...midfielders, player])
    //         } else {
    //             setMidfielders(midfielders => [...midfielders, player])
    //         }
    //     } else {
    //         if( forwards.length === 3 ) {
    //             forwards.shift()
    //             setForwards([...forwards, player])
    //         } else {
    //             setForwards(forwards => [...forwards, player])
    //         }
    //     }
    // }

    const addPlayer = (player) => {
        // addToTeam(player, goalkeepers, midfielders, defenders, forwards)
        setGoalkeepers(addToTeam(player, goalkeepers))
    }

    const getTeamValue = () => {
        let gkVal = 0, defVal = 0, midVal = 0, fwdVal = 0
        goalkeepers.forEach(gk => {
            gkVal += gk.now_cost
        })
        defenders.forEach(def => {
            defVal += def.now_cost
        })
        midfielders.forEach(mid => {
            midVal += mid.now_cost
        })
        forwards.forEach(fwd => {
            fwdVal += fwd.now_cost
        })

        return (gkVal + defVal + midVal + fwdVal) / 10
    }

    return (
    <div className>
        <PlayerSelector addPlayer={addPlayer}/>
        
        <div>
            Team Value: {getTeamValue()}
        </div>
        <div>
            Goalkeepers: {
                goalkeepers.map(gk => <div>{gk.web_name}</div>)
            }
        </div>
        <div>
            Defenders: {
                defenders.map(def => <div>{def.web_name}</div>)
            }
        </div>
        <div>
            Midfielders: {
                midfielders.map(mid => <div>{mid.web_name}</div>)
            }
        </div>
        <div>
            Forwards: {
                forwards.map(fwd => <div>{fwd.web_name}</div>)
            }
        </div>
    </div>
    )
}
