import React, { useState, useEffect } from 'react'
import get from '../modules/PlayerInfo'

export default function PlayerInfo({ player }){

    // const [opponents, setOpponents] = useState([])

    const opponents = []

    useEffect(() => {
        opponents = (get('fixtures', player, setOpponents))
    })

    console.log(opponents)

    return (
        <div>
            <img src={get('pic', player)} />
            <div>
                {player.first_name}
            </div>
            <div>
                {player.second_name}
            </div>
            <div>
                {player.total_points}
            </div>
            <div>
               {opponents.map(opponent => <div>{opponent}</div>)}
            </div>
        </div>
    )
}
