import React, { useState } from 'react'
import PlayerSelector from './PlayerSelector'
import addToTeam from '../utils/teamBuilderUtils'

export default function TeamSelector() {

    const [goalkeepers, setGoalkeepers] = useState([])
    const [defenders, setDefenders] = useState([])
    const [midfielders, setMidfielders] = useState([])
    const [forwards, setForwards] = useState([])
    const [teamValue, setTeamValue] = useState(0)

    const addPlayer = (player) => {
        const team = { goalkeepers, defenders, midfielders, forwards }
        switch (player.element_type) {
            case 1:
                const { newPlayers, newValue } = addToTeam(player, goalkeepers, 2, team)
                setGoalkeepers(newPlayers)
                console.log(newValue)
                setTeamValue(newValue)
                break          
            case 2:
                setDefenders(addToTeam(player, defenders, 5))
                break                 
            case 3:
                setMidfielders(addToTeam(player, midfielders, 5))
                break   
            case 4:
                setForwards(addToTeam(player, forwards, 3))
                break     
        }       
    }

    // const getTeamValue = () => {
    //     let gkVal = 0, defVal = 0, midVal = 0, fwdVal = 0
    //     goalkeepers.forEach(gk => {
    //         gkVal += gk.now_cost
    //     })
    //     defenders.forEach(def => {
    //         defVal += def.now_cost
    //     })
    //     midfielders.forEach(mid => {
    //         midVal += mid.now_cost
    //     })
    //     forwards.forEach(fwd => {
    //         fwdVal += fwd.now_cost
    //     })

    //     return (gkVal + defVal + midVal + fwdVal) / 10
    // }

    return (
    <div className>
        <PlayerSelector addPlayer={addPlayer}/>
        
        <div>
            Team Value: {teamValue}
        </div>
        <div>
            Goalkeepers: {
                goalkeepers.map(gk => <div>{gk.web_name}</div>)
            }
        </div>
        <div>
            Defenders: {
                defenders.map(def => <div>{def.web_name}</div>)
            }
        </div>
        <div>
            Midfielders: {
                midfielders.map(mid => <div>{mid.web_name}</div>)
            }
        </div>
        <div>
            Forwards: {
                forwards.map(fwd => <div>{fwd.web_name}</div>)
            }
        </div>
    </div>
    )
}
