import React from 'react'
import axios from 'axios'

export default function PlayerInfo({ player }){

    const getPlayerSrc = ({ photo }) => {
        const result = photo.replace('jpg', 'png')
        return 'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/110x140/p' + result ; 
    }


    const getPlayerFixtures = async ({ id }) => {
        const result = await axios.get('http://localhost:5000/players?fixtures=' + id)
        console.log(result)
        // return 'hello'
    }

    return (
        <div>
            <img src={getPlayerSrc(player)} />
            <div>
                {player.first_name}
            </div>
            <div>
                {player.second_name}
            </div>
            <div>
                {player.total_points}
            </div>
            <div>
               {getPlayerFixtures(player)}
            </div>
        </div>
    )
}
