import React, { useState } from 'react';
import './App.css';
import axios from 'axios'
import PlayerSelector from './components/PlayerSelector'
import TeamSelector from './components/TeamSelector'

function App() {

  const teamNames = ['Arsenal', 'Aston Villa', 'Bournemouth', 'Brighton', 'Burnley', 'Chelsea', 'Crystal Palace', 'Everton', 'Leicester City', 'Liverpool', 'Man City', 'Man Utd', 'Newcastle', 'Norwich', 'Sheffield Utd', 'Southampton', 'Spurs', 'Watford', 'West Ham', 'Wolves']

  const [teamId, setTeamId] = useState(1)
  const [playerId, setPlayerId] = useState(1)
  const [playerNames, setPlayerNames] = useState(["Mustafi", "Bellerín", "Kolasinac", "Maitland-Niles", "Sokratis", "Monreal", "Koscielny", "Mavropanos", "Jenkinson", "Holding", "Aubameyang", "Lacazette", "Nketiah", "Leno", "Özil", "Mkhitaryan", "Xhaka", "Torreira", "Elneny", "Guendouzi", "David Luiz", "Martínez", "Chambers", "Ceballos", "Pépé", "Nelson", "Willock", "Martinelli", "Tierney"])

  const handleSubmit = async (e) => {
    e.preventDefault()
    console.log(teamNames[teamId - 1])
    console.log(playerNames[playerId - 1])
    console.log(playerNames)
  }

  const updatePlayerNames = async () => {
    try {
      const { data: playerNames } = await axios.get(`http://localhost:5000/teams/${teamId}`) 
      setPlayerNames(playerNames)
    } catch (e) {
      console.log(e)
    }
  }

  const getPlayer = async () => {
    const { data: player } = await axios.get(`http://localhost:5000/players/${playerNames[playerId - 1]}`)
    return ( 
      <div>
        {player}
      </div>
    ) 
  }

  const handleTeamChange = (e) => {
    setTeamId(e.target.value)
    updatePlayerNames()
  }

  const handlePlayerChange = (e) => {
    setPlayerId(e.target.value)
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>

        <select onChange={handleTeamChange} value={teamId}>
        {
          teamNames.map((name, index) => <option value={ index + 1 }>{ name }</option>)
        }
        </select>
        <select onChange={handlePlayerChange}>
        {
          playerNames.map((name, index) => <option value={ index + 1 }>{ name }</option>)
        }
        </select>

        <input type='submit' value='Submit' />
      </form>
    </div>
  );
}

export default App;
