import React, { useState } from 'react'
import axios from 'axios'
import queryBackEnd from '../modules/PlayerSelector'

export default function PlayerSelector(props) {

  const teamNames = ['Arsenal', 'Aston Villa', 'Bournemouth', 'Brighton', 'Burnley', 'Chelsea', 'Crystal Palace', 'Everton', 'Leicester City', 'Liverpool', 'Man City', 'Man Utd', 'Newcastle', 'Norwich', 'Sheffield Utd', 'Southampton', 'Spurs', 'Watford', 'West Ham', 'Wolves']

  const [teamId, setTeamId] = useState(1)
  const [playerId, setPlayerId] = useState(1)
  const [playerNames, setPlayerNames] = useState(["Mustafi", "Bellerín", "Kolasinac", "Maitland-Niles", "Sokratis", "Monreal", "Koscielny", "Mavropanos", "Jenkinson", "Holding", "Aubameyang", "Lacazette", "Nketiah", "Leno", "Özil", "Mkhitaryan", "Xhaka", "Torreira", "Elneny", "Guendouzi", "David Luiz", "Martínez", "Chambers", "Ceballos", "Pépé", "Nelson", "Willock", "Martinelli", "Tierney"])

  const handleSubmit = async (e) => {
    e.preventDefault()
    const result = await queryBackEnd(playerId, 'team') 
    // getPlayer()

  }

  const handleTeamChange = async (e) => {
    //setTeamId(e.target.value)
    const result = await queryBackEnd(e.target.value, 'team') 
    setPlayerNames(result)
  }

  const handlePlayerChange = (e) => {
    setPlayerId(e.target.value)
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
           
        <select onChange={handleTeamChange} >
          <option>Select Team</option>
        {
          teamNames.map((name, index) => <option value={ index + 1 } key={index}>{ name }</option>)
        }
        </select>
        <select onChange={handlePlayerChange}>
          <option>Select Player</option>
        {
          playerNames.map((name, index) => <option value={ index + 1 } key={index}>{ name }</option>)
        }
        </select>

        <input type='submit' value='Add Player' />
      </form>
    </div>
  );
}
