export default function addToTeam(player, team) {
    const { element_type: position } = player
    const { goalkeepers, defenders, midfielders, forwards } = team
    switch (position) {
        case 1:
            return addGk(player, goalkeepers)
        case 2:
            return addDefOrMid(player, defenders)
        case 3:
            return addDefOrMid(player, midfielders)
        case 4:
            return addFwd(player, forwards)                                                    }
}

