import React, { useState } from 'react'
import PlayerSelector from './PlayerSelector'

export default function TeamSelector() {

    const [teamValue, setTeamValue] = useState(0)
    const [goalkeepers, setGoalkeepers] = useState([])
    const [defenders, setDefenders] = useState([])
    const [midfielders, setMidfielders] = useState([])
    const [forwards, setForwards] = useState([])

    const addPlayer = (player) => {
        console.log(player)
        const { now_cost, element_type } = player
        if (element_type === 1) {
            setGoalkeepers(goalkeepers => [...goalkeepers, player])
        } else if (element_type === 2) {
            setDefenders(defenders => [...defenders, player])
        } else if (element_type == 3) {
            setMidfielders(midfielders => [...midfielders, player])
        } else {
            setForwards(forwards => [...forwards, player])
        }

        setTeamValue(teamValue + now_cost)
        console.log(goalkeepers)
    }

    return (
    <div className>
        <PlayerSelector pos='1' addPlayer={addPlayer}/>
        <PlayerSelector pos='1' addPlayer={addPlayer}/>
        <PlayerSelector pos='2' addPlayer={addPlayer}/>
        <PlayerSelector pos='2' addPlayer={addPlayer}/>
        <PlayerSelector pos='2' addPlayer={addPlayer}/>
        <PlayerSelector pos='2' addPlayer={addPlayer}/>
        <PlayerSelector pos='2' addPlayer={addPlayer}/>
        <PlayerSelector pos='3' addPlayer={addPlayer}/>
        <PlayerSelector pos='3' addPlayer={addPlayer}/>
        <PlayerSelector pos='3' addPlayer={addPlayer}/>
        <PlayerSelector pos='3' addPlayer={addPlayer}/>
        <PlayerSelector pos='3' addPlayer={addPlayer}/>
        <PlayerSelector pos='4' addPlayer={addPlayer}/>
        <PlayerSelector pos='4' addPlayer={addPlayer}/>
        <PlayerSelector pos='4' addPlayer={addPlayer}/>
        <div>
            Team Value: {teamValue}
        </div>
        <div>
            Goalkeepers: {
                // goalkeepers[0] ? goalkeepers[0].forEach(gk => <div>{gk.web_name}</div>) : null
            }
        </div>
    </div>
    )
}
