import React, { useState } from 'react';
import './App.css';
import PlayerSelector from './components/PlayerSelector'
import TeamSelector from './components/TeamSelector'

function App() {

  const [team, setTeam] = useState('Arsenal')

  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(team)
  }

  const handleChange = (e) => {
    setTeam(e.target.value)
  }

  return (
    <div className="App">
      <form onSubmit={handleSubmit}>
        <select onChange={handleChange}>
          <option value='Man City' onChange={handleChange}>Man City</option>
          <option value='Liverpool' onChange={handleChange}>Liverpool</option>
          <option value='Man Utd' onChange={handleChange}>Man Utd</option>
        </select>
        <input type='submit' value='Submit' />
      </form>
    </div>
  );
}

export default App;
