import React, { useState } from 'react'
import SignUp from './SignUp'

export default function Login(props) {
    
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [errorMessage, setErrorMessage] =useState()

    const handleEmailChange = (e) => {
        setEmail(e.target.value)
    }

    const handlePasswordChange = (e) => {
        setPassword(e.target.value)
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
    }

    const displaySignUpPage = () => {
        body = <SignUp email={email} password={password}/>
    }

    let body = (
        <div>
        <form onSubmit={handleSubmit}>
            <div>
                <input type='text' value={email} onChange={handleEmailChange} placeholder='Email'></input>
            </div>
            <div>
                <input type='password' value={password} onChange={handlePasswordChange} placeholder='Password'></input>
            </div>

            <input type='submit' value='Login'/>
        </form>
        <button onClick={displaySignUpPage}>Sign Up</button>
        <div>
            {errorMessage}
        </div>
    </div>
    )

    return body 
}
