import React, { useState } from 'react'
import PlayerSelector from './PlayerSelector'
import addToTeam from '../modules/TeamSelector'

export default function TeamSelector() {

    const [goalkeepers, setGoalkeepers] = useState([])
    const [defenders, setDefenders] = useState([])
    const [midfielders, setMidfielders] = useState([])
    const [forwards, setForwards] = useState([])
    const [teamValue, setTeamValue] = useState(0)
    const [message, setMessage] = useState('')

    const addPlayer = (player) => {
        const team = { goalkeepers, defenders, midfielders, forwards }
        switch (player.element_type) {
            case 1:
                const { newPlayers: newGKs, newValue: newValue1, message: message1 = null } = addToTeam(player, goalkeepers, 2, team)
                setGoalkeepers(newGKs)
                setTeamValue(newValue1)
                setMessage(message1)
                break          
            case 2:
                const { newPlayers: newDEFs, newValue: newValue2, message: message2 = null } = addToTeam(player, defenders, 5, team)
                setDefenders(newDEFs)
                setTeamValue(newValue2)
                setMessage(message2)
                break                 
            case 3:
                const { newPlayers: newMIDs, newValue: newValue3, message: message3 = null } = addToTeam(player, midfielders, 5, team)
                setMidfielders(newMIDs)
                setTeamValue(newValue3)
                setMessage(message3)
                break   
            case 4:
                const { newPlayers: newFWDs, newValue: newValue4, message: message4 = null } = addToTeam(player, forwards, 3, team)
                setForwards(newFWDs)
                setTeamValue(newValue4)
                setMessage(message4)
                break     
        }       
    }

    const handleReset = () => {
        setGoalkeepers([])
        setDefenders([])
        setMidfielders([])
        setForwards([])
        setTeamValue(0)
    }

    return (
    <div>
        <PlayerSelector addPlayer={addPlayer}/>
        
        <div>
            Team Value: {teamValue}
        </div>
        <div>
            Goalkeepers: {
                goalkeepers.map(gk => <div>{gk.web_name}</div>)
            }
        </div>
        <div>
            Defenders: {
                defenders.map(def => <div>{def.web_name}</div>)
            }
        </div>
        <div>
            Midfielders: {
                midfielders.map(mid => <div>{mid.web_name}</div>)
            }
        </div>
        <div>
            Forwards: {
                forwards.map(fwd => <div>{fwd.web_name}</div>)
            }
        </div>
        <div>
            <button onClick={handleReset}>Clear</button>
        </div>
        <div>
            {message}
        </div>
    </div>
    )
}
