import React from 'react'
import axios from 'axios'

export default function PlayerInfo({ player }){

    const getPlayerSrc = ({ photo }) => {
        const result = photo.replace('jpg', 'png')
        return 'https://platform-static-files.s3.amazonaws.com/premierleague/photos/players/110x140/p' + result ; 
    }


    const getPlayerFixtures = async ({ id }) => {
        const { data: { fixtures: fixtureData } } = await axios.get('http://localhost:5000/players?fixtures=' + id)
        const upcomingFixtures = fixtureData.filter((fixture, index) => (index < 3))
        const opponentIds =  upcomingFixtures.map(fixture => fixture.is_home ?  fixture.team_a : fixture.team_h)
        const difficulties = upcomingFixtures.map(fixture => fixture.difficulty)
        const opponentNames = await opponentIds.map(id => axios.get(`http://localhost:500/teams?id=${id}`))
        console.log(opponentNames, difficulties)
    }

    return (
        <div>
            <img src={getPlayerSrc(player)} />
            <div>
                {player.first_name}
            </div>
            <div>
                {player.second_name}
            </div>
            <div>
                {player.total_points}
            </div>
            <div>
               <button onClick={() => getPlayerFixtures(player)}>fixtures</button>
            </div>
        </div>
    )
}
