import React, { useState } from 'react';
import './App.css';
import TeamSelector from './components/TeamSelector'
import Login from './components/Login'

function App() {

  const [user, setUser] = useState()

  const successfulLogin = (user) => {
    console.log(user)
    setUser(user)
  }

  const displayBody = () => {
    if(!user) {
      return (
        <div>
          <Login successfulLogin={successfulLogin} />
        </div>
      )
    }

    return <TeamSelector user={user} />
  }

  return displayBody()
}

export default App;
